// Using the simple bubble sort algorithm
function bubbleSort(arr) {
  var sorted = false;
  while (!sorted) {
    sorted = true;
    for (let i = 0; i < arr.length-1; i++) {
      if (arr[i] > arr[i+1]) {
        [arr[i], arr[i+1]] = [arr[i+1], arr[i]];
        sorted = false;
      }
    }
  }
  return arr;
}
