```java
public void SelectParticlesNonRecursive(int startIndexI, int startIndexJ, int selectedColorIndex)
{
    Queue<int> indexQueue = new Queue<int>();
    indexQueue.Enqueue(startIndexI + startIndexJ * level.Width);

    while (indexQueue.Count > 0)
    {
        int current = indexQueue.Dequeue();

        if (current < 0 || current >= level.Particles.Length || level.Particles[current].IsAddedList)
        {
            continue;
        }

        Particle currentParticle = level.Particles[current];

        if (currentParticle.ColorIndex != selectedColorIndex)
        {
            continue;
        }

        currentParticle.IsAddedList = true;
        selectedParticleList.Add(currentParticle);

        indexQueue.Enqueue(current + 1);
        indexQueue.Enqueue(current - 1);
        indexQueue.Enqueue(current + level.Width);
        indexQueue.Enqueue(current - level.Width);
    }
}
